-- CREAZIONE VISTA registryistitutiesteriview
IF EXISTS(select * from sysobjects where id = object_id(N'[dbo].[registryistitutiesteriview]') and OBJECTPROPERTY(id, N'IsView') = 1)
DROP VIEW [dbo].[registryistitutiesteriview]
GO

CREATE VIEW [dbo].[registryistitutiesteriview] AS 
SELECT CASE WHEN registry.active = 'S' THEN 'Si' WHEN registry.active = 'N' THEN 'No' END AS registry_active, registry.annotation AS registry_annotation,CASE WHEN registry.authorization_free = 'S' THEN 'Si' WHEN registry.authorization_free = 'N' THEN 'No' END AS registry_authorization_free, registry.badgecode AS registry_badgecode, registry.birthdate AS registry_birthdate, registry.ccp AS registry_ccp, registry.cf AS registry_cf, registry.ct AS registry_ct, registry.cu AS registry_cu, registry.email_fe AS registry_email_fe, registry.extension AS registry_extension, registry.extmatricula AS registry_extmatricula,CASE WHEN registry.flag_pa = 'S' THEN 'Si' WHEN registry.flag_pa = 'N' THEN 'No' END AS registry_flag_pa,CASE WHEN registry.flagbankitaliaproceeds = 'S' THEN 'Si' WHEN registry.flagbankitaliaproceeds = 'N' THEN 'No' END AS registry_flagbankitaliaproceeds, registry.foreigncf AS registry_foreigncf, registry.forename AS registry_forename,CASE WHEN registry.gender = 'S' THEN 'Si' WHEN registry.gender = 'N' THEN 'No' END AS registry_gender, registry.idaccmotivecredit AS registry_idaccmotivecredit, registry.idaccmotivedebit AS registry_idaccmotivedebit, registry.idcategory AS registry_idcategory, registry.idcentralizedcategory AS registry_idcentralizedcategory,
 [dbo].geo_city.title AS geo_city_title, registry.idcity, registry.idexternal AS registry_idexternal, registry.idmaritalstatus AS registry_idmaritalstatus,
 [dbo].geo_nation.title AS geo_nation_title, registry.idnation, registry.idreg, registry.idregistryclass AS registry_idregistryclass, registry.idregistrykind AS registry_idregistrykind, registry.idtitle AS registry_idtitle, registry.ipa_fe AS registry_ipa_fe, registry.ipa_perlapa AS registry_ipa_perlapa, registry.location AS registry_location, registry.lt AS registry_lt, registry.lu AS registry_lu, registry.maritalsurname AS registry_maritalsurname,CASE WHEN registry.multi_cf = 'S' THEN 'Si' WHEN registry.multi_cf = 'N' THEN 'No' END AS registry_multi_cf, registry.p_iva AS registry_p_iva, registry.pec_fe AS registry_pec_fe,
 [dbo].residence.description AS residence_description, registry.residence, registry.rtf AS registry_rtf, registry.sdi_defrifamm AS registry_sdi_defrifamm,CASE WHEN registry.sdi_norifamm = 'S' THEN 'Si' WHEN registry.sdi_norifamm = 'N' THEN 'No' END AS registry_sdi_norifamm, registry.surname AS registry_surname, registry.title, registry.toredirect AS registry_toredirect, registry.txt AS registry_txt, registry_istitutiesteri.city AS registry_istitutiesteri_city, registry_istitutiesteri.code AS registry_istitutiesteri_code, registry_istitutiesteri.ct AS registry_istitutiesteri_ct, registry_istitutiesteri.cu AS registry_istitutiesteri_cu,
 [dbo].nace.idnace AS nace_idnace, [dbo].nace.activity AS nace_activity, registry_istitutiesteri.idnace, registry_istitutiesteri.idreg AS registry_istitutiesteri_idreg, registry_istitutiesteri.institutionalcode AS registry_istitutiesteri_institutionalcode, registry_istitutiesteri.lt AS registry_istitutiesteri_lt, registry_istitutiesteri.lu AS registry_istitutiesteri_lu, registry_istitutiesteri.name AS registry_istitutiesteri_name, registry_istitutiesteri.referencenumber AS registry_istitutiesteri_referencenumber,
 isnull('Denominazione: ' + registry.title + '; ','') as dropdown_title
FROM [dbo].registry WITH (NOLOCK) 
INNER JOIN registry_istitutiesteri WITH (NOLOCK) ON registry.idreg = registry_istitutiesteri.idreg
LEFT OUTER JOIN [dbo].geo_city WITH (NOLOCK) ON registry.idcity = [dbo].geo_city.idcity
LEFT OUTER JOIN [dbo].geo_nation WITH (NOLOCK) ON registry.idnation = [dbo].geo_nation.idnation
LEFT OUTER JOIN [dbo].residence WITH (NOLOCK) ON registry.residence = [dbo].residence.idresidence
LEFT OUTER JOIN [dbo].nace WITH (NOLOCK) ON registry_istitutiesteri.idnace = [dbo].nace.idnace
WHERE  registry.idreg IS NOT NULL  AND registry_istitutiesteri.idreg IS NOT NULL 
GO

