-- CREAZIONE VISTA registryistitutiview
IF EXISTS(select * from sysobjects where id = object_id(N'[dbo].[registryistitutiview]') and OBJECTPROPERTY(id, N'IsView') = 1)
DROP VIEW [dbo].[registryistitutiview]
GO

CREATE VIEW [dbo].[registryistitutiview] AS SELECT CASE WHEN registry.active = 'S' THEN 'Si' WHEN registry.active = 'N' THEN 'No' END AS registry_active, registry.annotation AS registry_annotation,CASE WHEN registry.authorization_free = 'S' THEN 'Si' WHEN registry.authorization_free = 'N' THEN 'No' END AS registry_authorization_free, registry.badgecode AS registry_badgecode, registry.birthdate AS registry_birthdate, registry.ccp AS registry_ccp, registry.cf AS registry_cf, registry.ct AS registry_ct, registry.cu AS registry_cu, registry.email_fe AS registry_email_fe, registry.extension AS registry_extension, registry.extmatricula AS registry_extmatricula,CASE WHEN registry.flag_pa = 'S' THEN 'Si' WHEN registry.flag_pa = 'N' THEN 'No' END AS registry_flag_pa,CASE WHEN registry.flagbankitaliaproceeds = 'S' THEN 'Si' WHEN registry.flagbankitaliaproceeds = 'N' THEN 'No' END AS registry_flagbankitaliaproceeds, registry.foreigncf AS registry_foreigncf, registry.forename AS registry_forename,CASE WHEN registry.gender = 'S' THEN 'Si' WHEN registry.gender = 'N' THEN 'No' END AS registry_gender, registry.idcategory AS registry_idcategory, registry.idcentralizedcategory AS registry_idcentralizedcategory, [dbo].geo_city.title AS geo_city_title, registry.idcity, registry.idexternal AS registry_idexternal, registry.idmaritalstatus AS registry_idmaritalstatus, registry.idnation AS registry_idnation, registry.idreg, registry.idregistryclass AS registry_idregistryclass, registry.idregistrykind AS registry_idregistrykind, registry.idtitle AS registry_idtitle, registry.ipa_fe AS registry_ipa_fe, registry.ipa_perlapa AS registry_ipa_perlapa, registry.location AS registry_location, registry.lt AS registry_lt, registry.lu AS registry_lu, registry.maritalsurname AS registry_maritalsurname,CASE WHEN registry.multi_cf = 'S' THEN 'Si' WHEN registry.multi_cf = 'N' THEN 'No' END AS registry_multi_cf, registry.p_iva AS registry_p_iva, registry.pec_fe AS registry_pec_fe, registry.residence AS registry_residence, registry.rtf AS registry_rtf, registry.sdi_defrifamm AS registry_sdi_defrifamm,CASE WHEN registry.sdi_norifamm = 'S' THEN 'Si' WHEN registry.sdi_norifamm = 'N' THEN 'No' END AS registry_sdi_norifamm, registry.surname AS registry_surname, registry.title, registry.toredirect AS registry_toredirect, registry.txt AS registry_txt,CASE WHEN registry_istituti.active = 'S' THEN 'Si' WHEN registry_istituti.active = 'N' THEN 'No' END AS registry_istituti_active, registry_istituti.codicemiur AS registry_istituti_codicemiur, registry_istituti.codiceustat AS registry_istituti_codiceustat, registry_istituti.comune AS registry_istituti_comune, registry_istituti.ct AS registry_istituti_ct, registry_istituti.cu AS registry_istituti_cu, [dbo].istitutokind.tipoistituto AS istitutokind_tipoistituto, registry_istituti.idistitutokind AS registry_istituti_idistitutokind, registry_istituti.idistitutoustat AS registry_istituti_idistitutoustat, registry_istituti.idreg AS registry_istituti_idreg, registry_istituti.lt AS registry_istituti_lt, registry_istituti.lu AS registry_istituti_lu, registry_istituti.nome AS registry_istituti_nome, registry_istituti.sortcode AS registry_istituti_sortcode, registry_istituti.title AS registry_istituti_title, registry_istituti.title_en AS registry_istituti_title_en, isnull('Denominazione: ' + registry.title + '; ','') as dropdown_title FROM [dbo].registry WITH (NOLOCK)  INNER JOIN registry_istituti WITH (NOLOCK) ON registry.idreg = registry_istituti.idreg LEFT OUTER JOIN [dbo].geo_city WITH (NOLOCK) ON registry.idcity = [dbo].geo_city.idcity LEFT OUTER JOIN [dbo].istitutokind WITH (NOLOCK) ON registry_istituti.idistitutokind = [dbo].istitutokind.idistitutokind WHERE  registry.idreg IS NOT NULL  AND registry_istituti.idreg IS NOT NULL 
GO

